@pytest.mark.parametrize('fn', ([px.scatter, px.line, px.area, px.violin, px.box, px.strip] + [px.bar, px.funnel, px.histogram]))
@pytest.mark.parametrize('x,y,result', [('numerical', 'categorical', 'h'), ('categorical', 'numerical', 'v'), ('categorical', 'categorical', 'v'), ('numerical', 'numerical', 'v'), ('numerical', 'none', 'h'), ('categorical', 'none', 'h'), ('none', 'categorical', 'v'), ('none', 'numerical', 'v')])
def test_auto_orient_x_and_y(fn, x, y, result):
    series = dict(categorical=['a', 'a', 'b', 'b'], numerical=[1, 2, 3, 4], none=None)
    if ('none' not in [x, y]):
        assert (fn(x=series[x], y=series[y]).data[0].orientation == result)
    elif ((fn == px.histogram) or ((fn == px.bar) and ('categorical' in [x, y]))):
        assert (fn(x=series[x], y=series[y]).data[0].orientation != result)
    else:
        assert (fn(x=series[x], y=series[y]).data[0].orientation == result)

