@pytest.mark.parametrize('mode,options', [('ols', None), ('lowess', None), ('lowess', dict(frac=0.3)), ('rolling', dict(window=2)), ('expanding', None), ('ewm', dict(alpha=0.5))])
def test_trendline_results_passthrough(backend, mode, options):
    df = nw.from_native(px.data.gapminder(return_type=backend)).filter((nw.col('continent') == 'Oceania'))
    fig = px.scatter(df.to_native(), x='year', y='pop', color='country', trendline=mode, trendline_options=options)
    assert (len(fig.data) == 4)
    for trace in fig['data'][0::2]:
        assert ('trendline' not in trace.hovertemplate)
    for trendline in fig['data'][1::2]:
        assert ('trendline' in trendline.hovertemplate)
        if (mode == 'ols'):
            assert ('R<sup>2</sup>' in trendline.hovertemplate)
    results = px.get_trendline_results(fig)
    if (mode == 'ols'):
        assert (len(results) == 2)
        assert (set(results['country'].to_list()) == {'Australia', 'New Zealand'})
        result = results['px_fit_results'].values[0]
        assert (len(result.params) == 2)
    else:
        assert (len(results) == 0)

